    uint256[50] private __gap;
}","keccak256":"0x30231e8682866755b2f437baef3f608115f459f061bf87fd0975e3091b6619fd","license":"MIT"},"contracts/interfaces/ICrossSyncGateway.sol":{"content":"// SPDX-License-Identifier: MIT

